import '../../App.css';
import axios from 'axios';
import './SignUp.css';
import React, { useState } from 'react';

const SignUp= ()=>{

  const [name , setName] = useState({
         username:'',
         name:'',
         contact:'',
         password:"",
         confrimPassword:"",
         emailid:""
    });

    const  inputnameHandler= (e) =>{
      setName( {[e.target.name]: e.target.value} )
  }
  const  inputusernameHandler= (e) =>{
    setName( {[e.target.name]: e.target.value} )
}
const  inputemailidHandler= (e) =>{
  setName( {[e.target.name]: e.target.value} )
}
const  inputcontactHandler= (e) =>{
  setName( {[e.target.name]: e.target.value} )
}
const  inputpasswordHandler= (e) =>{
  setName( {[e.target.name]: e.target.value} )
}
const  inputconfirmHandler= (e) =>{
  setName( {[e.target.name]: e.target.value} )
}

  const handleSubmit = (e) => {
    e.preventDefault()
    axios.post('http://localhost:8081/carwash/adduser',name )
       .then(function(response) {
          console.log(response)
       })
       .catch(function(error) {
          console.log(error)
       })

      }

    return(
      <>
      <div className='in'>
      <div className='container'>    
          <h2 className='title'> Create Account</h2>
          <form className='form' onSubmit={handleSubmit}>
                <div className='input'>
                    <label>name:  </label>
                    <input type='text' value={name.name} onChange={inputnameHandler}/>
                </div>
                <div className='input'>
                    <label>Username:  </label>
                    <input type='text' value={name.username}onChange={inputusernameHandler}/>
                </div>
                <div className='input'>
                    <label>email id:  </label>
                    <input type='text'  value={name.emailid}onChange={inputemailidHandler}/>
                </div>
                <div classname='input'>
                    <label>contact: </label>
                    <input type='text'  value={name.contact}onChange={inputcontactHandler}/>
                </div>
                <div className='input'>
                    <label>password:  </label>
                    <input type='text'  value={name.password}onChange={inputpasswordHandler}/>
                </div>
                <div className='input'>
                    <label> confirm password:  </label>
                    <input type='text'  value={name.confrimPassword}onChange={inputconfirmHandler}/>
                </div>

                <div className='submit'>
                    <button value="submit" >submit</button>
                </div>
             <p>Do u have an account ?<a href='/login'>Login</a></p>
            </form>
            </div>
            </div>
      </>
    )
  }

export default SignUp;
