  constructor(props) {
    super(props)

    this.state = {
         username:'',
         name:'',
         contact:'',
         password:"",
         confrimPassword:"",
         emailid:"",
    }
}
handlenameChange = (e) => {
  this.setState({
    name : e.target.value
  })
  console.log("valy",this.state.name)
}
handleusernameChange = (e) => {
  this.setState({
    username : e.target.value
  })
}
handlecontactChange = (e) => {
