import React from 'react'


import { useNavigate } from 'react-router-dom';
import {toast} from 'react-toastify';
 
// Import toastify css file
import 'react-toastify/dist/ReactToastify.css';
 
 // toast-configuration method,
 // it is compulsor
 toast.configure()
const Payment = () => {
    let navigate = useNavigate();

     function paymentSuccess(){
        toast.success('Appointment Booked Successfully', {
            position: "top-center",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            });
            const notify = ()=>{
 
                // Calling toast method by passing string
                toast('Hello Geeks')
            }


    }
    return (
        <div>
           
                    
                        <h1 className='text-center'>PAYMENT</h1>
                        <div className="formDetails">
                            
                            <input type = "text" placeholder='Card Holder Name'/><br/><br/>
                            <input type="text"  placeholder='Card Number'/> <br/> <br/>
                            <input type="text" placeholder='MM/YY' /> <input type="password" placeholder='CVV'/>
                            <br/>
                            <br/>
                            <button onClick={notify} className=' paymentButton btn btn-primary'>Pay</button>
                        </div>
                  
        </div>
    )
}

export default Payment
