import React, { useState } from 'react'
import { toast } from 'react-toastify';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  let navigate = useNavigate();
  const [name, setName] = useState("");
  const [pass, setPss] = useState("");
  const [nameErr, setNameErr] = useState(false);
  const [passErr, setpassErr] = useState(false);


  const handleName = (e) => {
    setName(e.target.value)
    setNameErr(false)
  }
  const handlePass = (e) => {
    setPss(e.target.value)
    setpassErr(false)
  }
  const handleClick = () => {
    if (name && pass) {
      const data = {
        username: name,
        password: pass,
      }
      axios.post('http://localhost:8081/carwash/login', data)
        .then(function (response) {
          if (response && response.data) {
            if (response.data === "logged in") {
              navigate('/servicesAdmin')
            }
            else {
              alert("wrong credentials.")
            }
          }

        })
        .catch(function (error) {
          console.log(error)
        })

    }
    else if (name) {
      setpassErr(true);
      setNameErr(false);
    }
    else if (pass) {
      setNameErr(true);
      setpassErr(false);
    }
    else {
      setpassErr(true);
      setNameErr(true);
    }

  }
  return (
    <div>
      -<h1 className='text-center'>LOGIN</h1>
      <div className="formDetails">
        <input type="text" placeholder=' Name' value={name} onChange={handleName} />
        {nameErr && <span>Enter valid name</span>}
        <br /><br />
        <input type="password" placeholder='password' value={pass} onChange={handlePass} />
        {passErr && <span>Enter valid password</span>}
        <br />
        <br />
        <button className="bookBtn" onClick={handleClick}>Submit<i className=""></i></button>
      </div>


    </div>
  )
}

export default Login;
