import React ,{useState} from "react";
import axios from 'axios';

const AdminUpdate = () => {
    const [updatename, setupdateName] = useState("");
    const [updatecost, setUpdatecost] = useState("");
    const [updatedesc, setUpdateDesc] = useState("");
    localStorage.getItem('Name');
    const updateAdmin = (id) => {
        const data = {
            name: updatename,
            cost: updatecost,
            description: updatedesc
        }
        axios.put(`http://localhost:8082/admin/updatepacks/${id}`, data)
        .then(function (response) {
          if (response) {
           console.log(response)
          }

        })
        .catch(function (error) {
          console.log(error)
        })
      
    }

    return (
        <>
            <h1>update</h1>
            <input type="text" placeholder='name' value={localStorage.getItem('name')} onChange={(e) => setupdateName(e.target.value)} /><br /><br />
            <input type="text" placeholder='cost' value={  localStorage.getItem('cost')} onChange={(e) => setUpdatecost(e.target.value)} /><br /><br />
            <textarea placeholder='description' value={  localStorage.getItem('description')} onChange={(e) => setUpdateDesc(e.target.value)} />
            <br />
            <br />
            <button className="bookBtn" onClick={() => updateAdmin(localStorage.getItem('id'))} >Submit<i className=""></i></button>
        </>
    )
}
export default AdminUpdate;
