import '../../App.css';
import axios from 'axios';
import './SignUp.css';
import React, { useState } from 'react';

const SignUp= ()=>{

  const [inputField , setInputField] = useState({
         username:'',
         name:'',
         contact:'',
         password:"",
         confrimPassword:"",
         emailid:"",
    });

    const  inputsHandler= (event) => {
      setInputField({
         ...inputField,
         [event.target.name]: event.target.value
      })
   }

  const handleSubmit = (e) => {
    e.preventDefault()
    axios.post('http://localhost:8081/carwash/adduse',inputField )
       .then(function(response) {
          console.log(response)
       })
       .catch(function(error) {
          console.log(error)
       })

      }

    return(
      <>
      <div className='in'>
      <div className='container'>    
          <h2 className='title'> Create Account</h2>
          <form className='form' onSubmit={handleSubmit}>
                <div className='input'>
                    <label>name:  </label>
                    <input type='text' value={inputField.name} onChange={inputsHandler}/>
                </div>
                <div className='input'>
                    <label>Username:  </label>
                    <input type='text' value={inputField.username}onChange={inputsHandler}/>
                </div>
                <div className='input'>
                    <label>email id:  </label>
                    <input type='text'  value={inputField.emailid}onChange={inputsHandler}/>
                </div>
                <div classname='input'>
                    <label>contact: </label>
                    <input type='text'  value={inputField.contact}onChange={inputsHandler}/>
                </div>
                <div className='input'>
                    <label>password:  </label>
                    <input type='text'  value={inputField.password}onChange={inputsHandler}/>
                </div>
                <div className='input'>
                    <label> confirm password:  </label>
                    <input type='text'  value={inputField.confrimPassword}onChange={inputsHandler}/>
                </div>

                <div className='submit'>
                    <button value="submit" >submit</button>
                </div>
             <p>Do u have an account ?<a href='/login'>Login</a></p>
            </form>
            </div>
            </div>
      </>
    )
  }

export default SignUp;
