import React ,{useState} from 'react'
import {  toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    let navigate = useNavigate();
const [name,setName] = useState("");
const [pass,setPss] = useState("");
const [nameErr,setNameErr] = useState(false);


const handleName = (e) => {
setName(e.target.value)
setNameErr(false)
}
const handlePass = (e) => {
setPss(e.target.value)
setNameErr(false)
}
  const handleClick = () => {
    if(name && pass){
      navigate('/servicesss')
    }
    else{
      setNameErr(true)
    }

  }
    return (
        <div>


                        -<h1 className='text-center'>LOGIN</h1>
                        <div className="formDetails">

                            <input type = "text" placeholder=' Name' value={name} onChange={handleName}/>
                            {nameErr && <span>Enter valid name</span>}
                            <br/><br/>
                           <input type="password" placeholder='password' value={pass} onChange={handlePass}/>
                           {nameErr && <span>Enter valid password</span>}
                            <br/>
                            <br/>
                            <button className="bookBtn" onClick={handleClick}>Submit<i className=""></i></button>
                        </div>


        </div>
    )
}

export default Login;
