import React from 'react';
import '../../App.css';
import axios from 'axios';
import Services from './Services';

class Login extends React.Component {
  constructor(props) {
      super(props)

      this.state = {
           username:'',
           password:'',
           service: false,

      }
  }
  handleusernameChange = (e) => {
    this.setState({
      username : e.target.value
    })
  }
  handlepasswordChange = (e) => {
    this.setState({
      password : e.target.value
    })
  }
  handleSubmit= (event) =>{
    alert(`${this.state.username} ${this.state.password}`)
    const data = {
          username: this.state.username,
          password : this.state.password,

        }
        console.log("data",data)
       axios({
         method: 'post',
         url:"http://localhost:8081/carwash/login",
         data
       }).then((resp) => console.log("resp",resp))
       .catch(() => {})
       if(this.state.username && this.state.password){
         this.setState({
           service: true,
         })
       }
    event.preventDefault()
  }

  render(){
    return(
      <>
    <form className='login' onSubmit={this.handleSubmit}>
     <div>
       <h1>Login</h1>
                    <label> username</label>
                    <input className='uname' type='text' value={this.state.name} onChange={this.handleusernameChange}/>
                </div>
                <div>
                    <label>password</label>
                    <input className='pass' type='password' value={this.state.name}onChange={this.handlepasswordChange}/>
                </div>
                <div>
                    <button className='log' value="submit">submit</button>
                </div>
            </form>
            {
              this.state.service && <Services />
            }
      </>
    )
}
}
export default Login;
